import React,{ useState, useEffect} from 'react';
import { RouteComponentProps, useParams}  from 'react-router-dom';
import { Row, Col, Typography, Spin, DatePicker, Divider, Anchor, Menu } from "antd";
import styles from "./Detail.module.css";
import { commentMockData } from './mockup';
import { ProductDetailSlice } from '../../redux/productDetail/slice';
import { useSelector } from '../../redux/hooks';
import {
    Header,
    Footer,
    ProductIntro,
    ProductComments
  } from "../../components";
import axios from 'axios';
import MenuItem from 'antd/lib/menu/MenuItem';
import { useDispatch } from 'react-redux';
interface MatchParams{
    touristRouteId: string;
}

const { RangePicker } = DatePicker;
export const Detail : React.FC<RouteComponentProps<MatchParams>> = (props)=> {
    const {touristRouteId} = useParams<MatchParams>();
   // const [loading, setLoading] = useState<boolean>(true);
   // const [product,setProduct] = useState<any>(null);
    //const [error, setError] = useState<string | null>(null);
    const loading = useSelector(state=> state.productDetail.loading);
    const error = useSelector(state=> state.productDetail.error);
    const product = useSelector(state=> state.productDetail.data);

    const dispatch = useDispatch();
    useEffect(()=> {
        const fetchData= async()=> {
          dispatch(ProductDetailSlice.actions.fetchStart);

            try{
                const{data}= await axios.get(`http://123.56.149.216:8080/api/touristRoutes/${touristRouteId}`);
                dispatch(ProductDetailSlice.actions.fetchSuccess(data));
            }catch(err) {
                if(err instanceof Error) {
                    dispatch(ProductDetailSlice.actions.fetchFail(err.message))
                }
            }

        };

        fetchData();
    },[])

    if( loading) {
        return (
          <Spin
          size="large"
          style={{
            marginTop: 200,
            marginBottom: 200,
            marginLeft: "auto",
            marginRight:"auto",
            width: "100%"
          }}
          />
        )
      }
      if( error ) {
        return <div>something is error</div>
      }else {
    return (
        <div>
        <Header>

        </Header>
        <div className={styles["page-content"]}>
                <div className={styles["product-intro-container"]}>
                    <Row>
                        <Col span={13}>
                          <ProductIntro
                            title={product.title}
                            description={product.description}
                            price={product.originalPrice}
                            coupons={product.conpons}
                            points={product.points}
                            discount={product.price}
                            rating={product.rating}
                            pictures={product.touristRoutePictures.map(p=> p.url)}
                        /></Col>
                        <Col span={11}><RangePicker open style={{marginTop:20}}/></Col>
                    </Row>
                     </div>
                <Anchor className={styles["product-detail-anchor"]}>
                  <Menu mode="horizontal">
                    <MenuItem key="1"></MenuItem>
                    <Anchor.Link 
                      href='#feature' title="Product Features"
                    ></Anchor.Link>
                    <MenuItem key="2"></MenuItem>
                    <Anchor.Link 
                      href='#fees' title="Product Fees"
                    ></Anchor.Link>
                    <MenuItem key="3"></MenuItem>
                    <Anchor.Link 
                      href='#notes' title="Product Notes"
                    ></Anchor.Link>
                    <MenuItem key="4"></MenuItem>
                    <Anchor.Link 
                      href='#comments' title="User Comments"
                    ></Anchor.Link>
                  </Menu>
                </Anchor>
                <div id="feature" className={styles["product-detail-container"]}>
                  <Divider orientation="center">
                    <Typography.Title level={3}>Product Features</Typography.Title>
                  </Divider>
                  <div dangerouslySetInnerHTML={{__html:product.features}}
                  style={{margin:50}}
                  ></div>
                </div>
                <div id="fees" className={styles["product-detail-container"]}>
                <Divider orientation="center">
                    <Typography.Title level={3}>Product Fees</Typography.Title>
                  </Divider>
                  <div dangerouslySetInnerHTML={{__html:product.fees}}
                  style={{margin:50}}
                  ></div>
                </div>
                <div id="notes" className={styles["product-detail-container"]}>
                <Divider orientation="center">
                    <Typography.Title level={3}>Product Notes</Typography.Title>
                  </Divider>
                  <div dangerouslySetInnerHTML={{__html:product.notes}}
                  style={{margin:50}}
                  ></div>
                </div>
                <div id="comments" className={styles["product-detail-container"]}>
                <Divider orientation="center">
                    <Typography.Title level={3}>User Comments</Typography.Title>
                  </Divider>
                 <div style={{margin:40}}>
                   <ProductComments
                     data={commentMockData}
                   />
                 </div>
                </div>
            </div>
        <Footer></Footer>
        </div>
    )
      }
}
