import React,{ useState, useEffect} from 'react';
import { RouteComponentProps, useParams}  from 'react-router-dom';
import { Row, Col, Typography, Spin, DatePicker, Divider } from "antd";
import styles from "./Detail.module.css";
import {
    Header,
    Footer,
    ProductIntro,
    ProductComments
  } from "../../components";
import axios from 'axios';
interface MatchParams{
    touristRouteId: string;
}

const { RangePicker } = DatePicker;
export const Detail : React.FC<RouteComponentProps<MatchParams>> = (props)=> {
    const {touristRouteId} = useParams<MatchParams>();
    const [loading, setLoading] = useState<boolean>(true);
    const [product,setProduct] = useState<any>(null);
    const [error, setError] = useState<string | null>(null);

    useEffect(()=> {
        const fetchData= async()=> {
            setLoading(true);
            try{
                const{data}= await axios.get(`http://123.56.149.216:8080/api/touristRoutes/${touristRouteId}`);
                setProduct(data);
                setLoading(false);
            }catch(err) {
                if(err instanceof Error) {
                    setError(err.message);
                    setLoading(false);
                }
            }

        };

        fetchData();
    },[])

    if( loading) {
        return (
          <Spin
          size="large"
          style={{
            marginTop: 200,
            marginBottom: 200,
            marginLeft: "auto",
            marginRight:"auto",
            width: "100%"
          }}
          />
        )
      }
      if( error ) {
        return <div>something is error</div>
      }else {
    return (
        <div>
        <Header>

        </Header>
        <div className={styles["page-content"]}>
                <div className={styles["product-intro-container"]}>
                    <Row>
                        <Col span={13}>
                          <ProductIntro
                            title={product.title}
                            description={product.description}
                            price={product.originalPrice}
                            coupons={product.conpons}
                            points={product.points}
                            discount={product.price}
                            rating={product.rating}
                            pictures={product.touristRoutePictures.map(p=> p.url)}
                        /></Col>
                        <Col span={11}><RangePicker open style={{marginTop:20}}/></Col>
                    </Row>
                     </div>
                <div className={styles["product-detail-anchor"]}></div>
                <div id="feature" className={styles["product-detail-container"]}>
                  <Divider orientation="center">
                    <Typography.Title level={3}>product features</Typography.Title>
                  </Divider>
                  <div dangerouslySetInnerHTML={{__html:product.features}}
                  style={{margin:50}}
                  ></div>
                </div>
                <div id="fees" className={styles["product-detail-container"]}>
                <Divider orientation="center">
                    <Typography.Title level={3}>product fees</Typography.Title>
                  </Divider>
                  <div dangerouslySetInnerHTML={{__html:product.fees}}
                  style={{margin:50}}
                  ></div>
                </div>
                <div id="notes" className={styles["product-detail-container"]}>
                <Divider orientation="center">
                    <Typography.Title level={3}>product notes</Typography.Title>
                  </Divider>
                  <div dangerouslySetInnerHTML={{__html:product.notes}}
                  style={{margin:50}}
                  ></div>
                </div>
                <div id="comments" className={styles["product-detail-container"]}></div>
            </div>
        <Footer></Footer>
        </div>
    )
      }
}
